import {
  FETCH_PHONES_START,
  FETCH_PHONES_SUCCESS,
  FETCH_PHONES_FAILURE
} from 'actionTypes'
import { fetchPhones as fetchPhonesApi } from 'api'

// методы, который получает dispatch как параметр и можем передавать ещё доп аргументы
// это redux-thunk - action как функции, для цепочки
export const fetchPhones = () => async dispatch => {
  // мы хотим задиспатчить phones
  dispatch({
    type: FETCH_PHONES_START
  })

  try {
    const phones = await fetchPhonesApi()
    dispatch({
      type: FETCH_PHONES_SUCCESS,
      payload: phones
    })
  } catch (error) {
    dispatch({
      type: FETCH_PHONES_FAILURE,
      payload: error,
      error: true
    })
  }
}