import React, { Component } from 'react'
import { connect } from 'react-redux' // чтобы могли деспатчить 

import { fetchPhones } from 'actions'

class Phones extends Component {
  componentDidMount() {
    this.props.fetchPhones() // action, который будет fetch'ить
  }

  render() {
    return <div>Phones</div>
  }
}


// https://maxfarseer.gitbooks.io/redux-course-ru-v2/content/sozdanie/redyuseri-i-connect.html
// connect - function connects a React component to a Redux store
// подключи React компонент к Redux store
// Результат работы функции connect - новый присоединенный компонент, который оборачивает переданный компонент.

// function mapStateToProps (state) {
//   return {
//     user: state
//   }
// }

// через connect попадёт в this.props
// дожны бытьactions
const mapDispatchToProps = {
  fetchPhones
}

export default connect(null, mapDispatchToProps)(Phones)
 